yaml_content:
name: simple-login-page
description: "YAML wrapping a simple responsive HTML login page"
filename: login.html
mime_type: text/html
content: |
  <!doctype html>
  <html lang="en">
  <head>
    <meta charset="utf-8" />
    <meta name="viewport" content="width=device-width,initial-scale=1" />
    <title>Simple Login</title>
    <style>
      :root { --bg:#f5f7fb; --card:#ffffff; --accent:#2563eb; --muted:#6b7280; }
      *{box-sizing:border-box}
      body{
        margin:0;
        font-family:system-ui,-apple-system,Segoe UI,Roboto,"Helvetica Neue",Arial;
        background:linear-gradient(180deg,var(--bg),#e9eef8);
        display:flex;
        align-items:center;
        justify-content:center;
        min-height:100vh;
        padding:24px;
      }
      .card{
        background:var(--card);
        width:100%;
        max-width:420px;
        padding:28px;
        border-radius:12px;
        box-shadow:0 8px 30px rgba(18,38,63,0.08);
      }
      h1{margin:0 0 8px;font-size:20px}
      p.lead{margin:0 0 18px;color:var(--muted);font-size:14px}
      label{display:block;font-size:13px;margin-bottom:6px;color:#111827}
      input[type="text"],input[type="password"]{
        width:100%;
        padding:10px 12px;
        border:1px solid #e6e9ef;
        border-radius:8px;
        font-size:15px;
      }
      .row{margin-bottom:14px}
      .actions{display:flex;align-items:center;justify-content:space-between;gap:12px}
      button{
        background:var(--accent);
        color:#fff;
        border:0;
        padding:10px 14px;
        border-radius:10px;
        font-size:15px;
        cursor:pointer;
      }
      button.secondary{
        background:transparent;
        color:var(--muted);
        border:1px solid #e6e9ef;
      }
      .small{font-size:13px;color:var(--muted)}
      .form-foot{margin-top:12px;display:flex;justify-content:space-between;align-items:center}
      @media (max-width:420px){ .card{padding:20px} }
    </style>
  </head>
  <body>
    <main class="card" role="main" aria-labelledby="loginHeading">
      <h1 id="loginHeading">Sign in</h1>
      <p class="lead">Enter your credentials to access your account.</p>

      <form id="loginForm" action="#" method="post" novalidate>
        <div class="row">
          <label for="email">Email or username</label>
          <input id="email" name="email" type="text" autocomplete="username" required placeholder="you@example.com" />
        </div>

        <div class="row">
          <label for="password">Password</label>
          <input id="password" name="password" type="password" autocomplete="current-password" required placeholder="••••••••" />
        </div>

        <div class="form-foot">
          <label class="small"><input type="checkbox" name="remember" /> Remember me</label>
          <a class="small" href="#" onclick="alert('Forgot password flow not implemented');return false;">Forgot?</a>
        </div>

        <div style="height:14px"></div>

        <div class="actions">
          <button type="submit">Sign in</button>
          <button type="button" class="secondary" onclick="alert('Register flow not implemented')">Register</button>
        </div>
      </form>

      <script>
        document.getElementById('loginForm').addEventListener('submit', function(e){
          e.preventDefault();
          const email = document.getElementById('email').value.trim();
          const pw = document.getElementById('password').value;
          if(!email || !pw){
            alert('Please fill both fields.');
            return;
          }
          alert('Demo only: credentials not sent.\nEmail/username: ' + email);
        });
      </script>
    </main>
  </body>
  </html>
